import React, { useState } from 'react';
import ChatbotInput from './Chat/ChatbotInput';
import ChatbotMessage from './Chat/ChatbotMessage';
import NewsButtonsChatbot from './Chat/ChatbotMessage';

import '../styles/Chatbot.css';
import { mockData } from '../../mockData.js';  // Import the mock data

const Chatbot = ({ selectedCategories }) => {
  const [messages, setMessages] = useState([]);
  const [filteredNewsData, setFilteredNewsData] = useState([]);

  const handleUserMessage = (message) => {
    // Check if the user is asking for technology-related news items
    if (message.toLowerCase().includes('technology')) {
      filterNewsData('Technology');
    } else if (message.toLowerCase().includes('politics')) {
      filterNewsData('Politics');
    }
    else if (message.toLowerCase().includes('sports')) {
      filterNewsData('Sports');
    }
    else if (message.toLowerCase().includes('entertainment')) {
      filterNewsData('Entertainment');
    }
  };

  const handleButtonClick = (category) => {
    if (category === 'Technology') {
      filterNewsData(category);
    }
    else if (category === 'Politics') {
      filterNewsData(category);
    }
    else if (category === 'Sports') {
      filterNewsData(category);
    }
    else if (category === 'Entertainment') {
      filterNewsData(category);
    }
  };

  const handleSendMessage = (message) => {
    // Add user's message to chat
    setMessages([...messages, { content: message, isUser: true }]);
    handleUserMessage(message);  // Process user's message
  };

  const filterNewsData = (filter) => {
    const filteredData = mockData.filter(article =>
      article.category.toLowerCase() === filter.toLowerCase()
    );
    // Append technology news to chat messages
    const technologyNewsMessages = filteredData.map(article => ({
      content: `${article.title}: ${article.content}`,
      isUser: false,
    }));
    setMessages(prevMessages => [...prevMessages, ...technologyNewsMessages]);
    setFilteredNewsData(filteredData);
  };

  return (
    <div className="chatbot">
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <ChatbotMessage key={index} message={msg.content} isUser={msg.isUser} />
        ))}
      </div>
      <div className="category-buttons">
       <NewsButtonsChatbot selectedCategories={selectedCategories}/>
        {selectedCategories.map(category => (
          <button className = "buttonNews" key={category} onClick={() => handleButtonClick(category)} >
           <p> Give me the news about </p> {category}
          </button>
        ))}
      </div>
      <ChatbotInput onSendMessage={handleSendMessage} />
    </div>
  );
}

export default Chatbot;
