import React, { useState } from 'react';
// import ChatbotInput from './Chat/ChatbotInput';
// import ChatbotMessage from './Chat/ChatbotMessage';
import { useParams } from 'react-router-dom';

import '../styles/Chatbot.css';
import {  } from '../../mockData.js';  // Import the mock data

const Chatbot = ({ selectedCategories }) => {
  const [messages, setMessages] = useState([]);
  const [filteredNewsData, setFilteredNewsData] = useState([]);
  const {participantID} = useParams();
  let {age} = useParams();
  let {location} = useParams();
  let {gender} = useParams();
  let {education} = useParams();
  let {condition} = useParams();
  // The src string with the participantID dynamically included
  const iframeSrc = `https://europe.webchat.botframework.com/embed/JosBotNL?s=0RTzI3dqyBM.7Jjjkfy5Xmgu6DaGi8SbSMvhytqaE4uy5CQvCOaDMFc&userID=${participantID}test&username=josbot_${condition}`;

  const handleUserMessage = (message) => {
    // Check if the user is asking for technology-related news items
    if (message.toLowerCase().includes('technologie')) {
      filterNewsData('Technologie');
    } else if (message.toLowerCase().includes('Politiek')) {
      filterNewsData('Politiek');
    }
    else if (message.toLowerCase().includes('Sport')) {
      filterNewsData('Sport');
    }
    else if (message.toLowerCase().includes('Amusement')) {
      filterNewsData('Amusement');
    }
  };

  const handleButtonClick = (category) => {
    if (category === 'Technologie') {
      filterNewsData(category);
    }
    else if (category === 'Politiek') {
      filterNewsData(category);
    }
    else if (category === 'Sport') {
      filterNewsData(category);
    }
    else if (category === 'Amusement') {
      filterNewsData(category);
    }
  };

  const handleSendMessage = (message) => {
    // Add user's message to chat
    setMessages([...messages, { content: message, isUser: true }]);
    handleUserMessage(message);  // Process user's message
  };

  const filterNewsData = (filter) => {
    const filteredData = mockData.filter(article =>
      article.category.toLowerCase() === filter.toLowerCase()
    );
    // Append technology news to chat messages
    const technologyNewsMessages = filteredData.map(article => ({
      content: `${article.title}: ${article.content}`,
      isUser: false,
    }));
    setMessages(prevMessages => [...prevMessages, ...technologyNewsMessages]);
    setFilteredNewsData(filteredData);
  };

  return (
    <div className="chatbot">

      <iframe
        src={iframeSrc} 
         style={{ minWidth: '300px', width: '100%', minHeight: '700px' }}
      ></iframe>
    </div>
  );
}

export default Chatbot;
