import React, { useState } from 'react';
import '../styles/Website.css';  // Importing a stylesheet
import NewsButtons from './NewsButtons.js';  // Importing the NewsButtons component

const Website =({ selectedCategories, newsData, fetchArticles }) => {
    const [selectedArticleId, setSelectedArticleId] = useState(null);
    const [filteredCategory, setFilteredCategory] = useState(null);

    const handleArticleClick = (id) => {
        if (selectedArticleId === id) {
            setSelectedArticleId(null); // Collapse the article if clicked again
        } else {
            setSelectedArticleId(id); // Set the selected article ID
        }
    };

    const filterArticlesByCategory = (category) => {
        setFilteredCategory(category);
        fetchArticles(category);
    };

    return (
        <div className="website-container">
            <NewsButtons selectedCategories={selectedCategories} onCategoryClick={filterArticlesByCategory}/>
            <ul className="news-list">
                {newsData.filter(article => filteredCategory ? article.category === filteredCategory : true).map(article => (
                    <div key={article.id} className="news-article" onClick={() => handleArticleClick(article.id)}>
                       <div className="container">
                            <div className="header">
                                <h2>{article.title}</h2>

                            </div>

                            <span>
                                    <svg width="16" height="16" viewBox="0 0 16 16">
                                        <path d="M8 9L4 5h8l-4 4z"></path>
                                    </svg>
                                </span>
                                <div className="image-container">
                                        {/* <img src={article.image} alt={article.title} /> */}
                            </div>    
                        </div>
                        {selectedArticleId === article.id && <p>{article.content}</p>}

                </div>
                ))}
            </ul>
        </div>
    );
};

export default Website;
