import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useParams } from 'react-router-dom';
// import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';
import RegistrationForm from './components/RegistrationForm.js';
import UserProfile from './components/Profile.js';
import userIcon from './components/images/user.svg';
import Chatbot from './components/Chatbot/Chatbot.js';

import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [selectedtags, setSelectedtags] = useState([]);
  const [articles, setArticles] = useState([]);
  const [userProfile, setUserProfile] = useState({}); // Store user profile data

 // State variables for query parameters
//  const params = useParams(); // Get params from the URL
//  const { id } = params; // Extract condition from params
//  const { age } = params; // Extract condition from params
//  const { location } = params; // Extract condition from params
//  const { gender } = params; // Extract condition from params
//  const { education } = params; // Extract condition from params
//  const { condition } = params; // Extract condition from params


//  useEffect(() => {
//   handleLogin(params);
// }, [params]);

// const handleLogin = (params) => {
//   const allParamsPresent = Object.values(params).every(param => param);
//   if (allParamsPresent) {
//     setIsLoggedIn(true);
//   } else {
//     console.error('Missing required URL parameters.');
//   }
// };
// Function to handle user login
const handleLogin = () => {
  // Your login logic here
  // Assuming you get user profile data including id, age, etc.
  const profileData = {
    // ... user data
  };
  setUserProfile(profileData);
  setIsLoggedIn(true);
};

const handlePreferencesSubmit = (tags) => {
    setSelectedtags(tags);
    axios.post('http://127.0.0.1:5001/api/recommend', { tags: tags })
      .then(response => {
        // Handle the response containing the recommendations
        setArticles(response.data.similar_articles);
      })
      .catch(error => {
        console.error('Error fetching recommendations:', error);
      });
  };

  // Redirect based on condition parameter
  // const renderRedirect = () => {
  //   if (condition === 'condition1') {
  //     return <Chatbot />;
  //   } else if (condition === 'condition2') {
  //     return <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />;
  //   } else {
  //     // Assuming handlePreferencesSubmit is defined in your component
  //     return <UserProfile />;
  //   }
  // };

  return (
    <Router>
      <div className="App">
        {isLoggedIn && (
          <header className='header'>
            <nav className='nav'>
            <ul>
              {/* Use stored profile data for constructing the links */}
            <Link to={`/profile/${userProfile.id}/${userProfile.age}/${userProfile.location}/${userProfile.gender}/${userProfile.education}/${userProfile.condition}`}>Profile</Link>
            {/* ... other links */}
            </ul>
            </nav>
          </header>
        )}
        <Routes>
        <Route path="/register/:id/:age/:location/:gender/:education/:condition" element={<RegistrationForm />} />
          <Route path="/news-preferences/:id/:age/:location/:gender/:education/:condition" element={<NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />} />
          <Route path="/website/:id/:age/:location/:gender/:education/:condition" element={<Website selectedtags={selectedtags} articles={articles} />} />
          <Route path="/chatbot/:id/:age/:location/:gender/:education/:condition" element={<Chatbot  articles={articles} />} />
          {/* <Route path="/:id/:age/:location/:gender/:education/:condition" element={isLoggedIn ? renderRedirect() : <Navigate to="/login" replace />} /> */}
          <Route path="/profile/:id/:age/:location/:gender/:education/:condition" element={<UserProfile   />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
