import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';
import RegistrationForm from './components/RegistrationForm.js';
import UserProfile from './components/Profile.js';
import userIcon from './components/images/user.svg';
import Chatbot from './components/Chatbot/Chatbot.js';

import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [selectedtags, setSelectedtags] = useState([]);
  //const [allArticles, setAllArticles] = useState([]); // This state will hold all articles
  const [articles, setArticles] = useState([]); // This state will hold only the articles that match the selected tags

  const handleLogin = () => {
    // Create an instance of URLSearchParams with the current URL's query string
    const queryParams = new URLSearchParams(window.location.search);

    // Check if all required parameters are present
    const hasAllParams = ['id', 'location', 'age', 'gender', 'education', 'condition']
      .every(param => queryParams.has(param));

    if (hasAllParams) {
      setIsLoggedIn(true);
    } else {
      // Handle the case where not all parameters are present
      // For example, you might want to show an error message or redirect the user
      console.error('Missing required URL parameters.');
    }
  };


  const handlePreferencesSubmit = (tags) => {
    setSelectedtags(tags);
    axios.post('http://127.0.0.1:5001/api/recommend', { tags: tags })
      .then(response => {
        // Handle the response containing the recommendations
        setArticles(response.data.similar_articles);
      })
      .catch(error => {
        console.error('Error fetching recommendations:', error);
      });
  };


  return (
    <Router>
      <div className="App">
          {isLoggedIn && ( // Render the header only if the user is logged in
          <header className='header'>
            <nav className='nav'>
              <ul>
                <li className='home'><Link to="/" element = {<NewsPreferences/>}>Home</Link></li>
                <li className='profile'><Link to="//profile/:id/:location/:age/:gender/:education/:condition" element = {<UserProfile/>}><img src={userIcon} alt="User Icon" /></Link></li>
              </ul>
            </nav>
          </header>
        )}
        <Routes>
          <Route path="/register/:id/:location/:age/:gender/:education/" element={<RegistrationForm />} />
          {/* <Route path="/login/:id/:location/:age/:gender/:education" element={isLoggedIn ? <Navigate to="/news-preferences" replace /> : <LoginForm onLogin={handleLogin} />} /> */}
          <Route path="/news-preferences/:id/:location/:age/:gender/:education/condition2" element = {<NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />} />
          <Route path="/website/:id/:location/:age/:gender/:education/condition2" element={<Website selectedtags={selectedtags} articles={articles} />} />
          <Route path="/chatbot/:id/:location/:age/:gender/:education/condition1" element={<Chatbot selectedtags={selectedtags} articles={articles} />} />
          <Route path="/" element={isLoggedIn ? <Navigate to="/news-preferences" replace /> : <Navigate to="/login" replace />} />
          <Route path="/profile/:id/:location/:age/:gender/:education/:condition" element={<UserProfile /> } />

        </Routes>
      </div>
    </Router>
  );
};

export default App;
