import './App.css';
import Chatbot from './components/Chatbot/Chatbot.js';
import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';  // Importing the NewsPreferencesForm component
import { mockData } from './mockData';  // Import mock data
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RegistrationForm from './components/RegistrationForm.js';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';


const App = () => {
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isChatbot, setIsChatbot] = useState(false);
  const [hasSelectedPreferences, setHasSelectedPreferences] = useState(false);  // New state for tracking preference selection
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [articles, setArticles] = useState([]);
  const [filteredCategory, setFilteredCategory] = useState(null);
  const [isRegistered, setIsRegistered] = useState(false);


  const fetchArticles = () => {
    axios.get('http://127.0.0.1:5000/api/data',{
      headers: {
        'Content-Type': 'application/json'
      }})
      .then(response => {
        setArticles(response.data);
        console.log(response);
      })
      .catch(error => {
        console.error('There was an error fetching the data:', error);
      });
  };
  useEffect(() => {
    fetchArticles();
  }, []); // Empty array means this useEffect runs once when component mounts



  // Call fetchArticles when currentCategory changes
  const filterArticlesByCategory = (category) => {
    setFilteredCategory(category);
    fetchArticles(category);
  };

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    // Assume if username is "chatbot" then show chatbot, else show website
    setIsChatbot(username.toLowerCase() === 'chatbot');
  };

const handlePreferencesSubmit = (categories) => {
  setSelectedCategories(categories);
};
  // useEffect(() => {
  //   // Fetch the message from the Flask server
  //   axios.get('http://127.0.0.1:5000/:5000/api/hello')
  //   .then(response => {
  //       console.log(response.data);
  //   })
  //   .catch(error => {
  //       console.error('There was an error fetching the data:', error);
  //   })
  // }
  // );

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {message}
        </header>
        <div className="format">
          <Switch>
            <Route path="/register">
              {isRegistered ? <Redirect to="/login" /> : <RegistrationForm onRegister={() => setIsRegistered(true)} />}
            </Route>
            <Route path="/login">
              {isLoggedIn ? <Redirect to="/news-preferences" /> : <LoginForm onLogin={handleLogin} />}
            </Route>
            <Route path="/news-preferences">
              {!isLoggedIn ? <Redirect to="/login" /> : <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />}
            </Route>
            {/* Define other routes here */}
            {/* Redirect from root to either login or news-preferences based on isLoggedIn */}
            <Route exact path="/">
              {isLoggedIn ? <Redirect to="/news-preferences" /> : <Redirect to="/login" />}
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
};

export default App;
