import React, { useState } from 'react';
import '../styles/Website.css';
import NewsButtons from './NewsButtons.js';

const Website = ({ selectedCategories, filteredArticles }) => {
    const [selectedArticleId, setSelectedArticleId] = useState(null);

    const handleArticleClick = (id) => {
        setSelectedArticleId(prevId => prevId === id ? null : id);
    };

    return (
        <div className="website-container">
            <NewsButtons selectedCategories={selectedCategories} />
            <ul className="news-list">
                {filteredArticles.map((article, index) => {
                    // Assuming articles is an array of arrays, with structure based on your previous data
                    const id = index; // Using index as a fallback ID
                    const title = article[0]; // Assuming the first element is the title
                    const content = article[2]; // Assuming the third element is the content
                    const image = article[1]; // Assuming the second element is the image URL

                    return (
                        <li key={id} className={`news-article ${selectedArticleId === id ? 'open' : ''}`} onClick={() => handleArticleClick(id)}>
                            <div className="header">
                                <h2>{title}</h2>
                            </div>
                            {selectedArticleId === id && (
                                <>
                                    <div className="image-container">
                                        {/* Conditional rendering if image exists */}
                                        {image && <img src={image} alt={title} />}
                                    </div>
                                    <p>{content}</p>
                                </>
                            )}
                        </li>
                    );
                })}
            </ul>
        </div>
    );
};

export default Website;
