import React, { useState } from 'react';
import ChatbotInput from './Chat/ChatbotInput';
import ChatbotMessage from './Chat/ChatbotMessage';
import '../styles/Chatbot.css';
import { mockData } from '../mockData';  // Import the mock data

const Chatbot = ({ selectedCategories }) => {
  const [messages, setMessages] = useState([]);
  const [filteredNewsData, setFilteredNewsData] = useState([]);

  const handleUserMessage = (message) => {
    // Example: Provide different responses based on selected categories
    if (selectedCategories.includes('Technology')) {
      // Respond with technology-related information
    } else if (selectedCategories.includes('Sports')) {
      // Respond with sports-related information
    }
    // ... handle other categories
  };
  const handleSendMessage = (message) => {
    // Add user's message to chat
    setMessages([...messages, { content: message, isUser: true }]);

    // After sending a message, you can add logic here to get a response from a chatbot API.
    // For now, let's just add a dummy reply.
    setMessages([...messages, 
      { content: message, isUser: true },
      { content: 'Hello, I am your chatbot!', isUser: false }
    ]);
  };

  return (
    <div className="chatbot">
      <div className="category-buttons">
        {selectedCategories.map(category => (
          <button key={category} onClick={() => handleButtonClick(category)}>
            {category}
          </button>
        ))}
      </div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <ChatbotMessage key={index} message={msg.content} isUser={msg.isUser} />
        ))}

      </div>

      <ChatbotInput onSendMessage={handleSendMessage} />
      <div className="filtered-news">
        {filteredNewsData.map(article => (
          <div key={article.id}>
            <h2>{article.title}</h2>
            <p>{article.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Chatbot;
