import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';
import RegistrationForm from './components/RegistrationForm.js';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState([]);
  //const [allArticles, setAllArticles] = useState([]); // This state will hold all articles
  const [articles, setArticles] = useState([]); // This state will hold only the articles that match the selected categories

  // Function to fetch all articles when the app loads
  const fetchArticles = () => {
    axios.get('http://127.0.0.1:5000/api/data')
      .then(response => {
        console.log(response);
        setArticles(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the data:', error);
      });
  };

  useEffect(fetchArticles, []); // Run once on mount

  // Function to fetch articles based on selected categories
  const fetchArticlesByCategory = (categories) => {
    // Add logic here to fetch articles by category from your API
    // For now, we're just filtering the already fetched articles for simplicity
    const filteredArticles = articles.filter(article => categories.includes(article.category));
    setArticles(filteredArticles);
    console.log()
  };

  const handleLogin = (username) => {
    setIsLoggedIn(true);
  };

  const handlePreferencesSubmit = (categories) => {
    setSelectedCategories(categories);
    fetchArticlesByCategory(categories); // Fetch and set the filtered articles
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/register" element={<RegistrationForm />} />
          <Route path="/login" element={isLoggedIn ? <Navigate to="/news-preferences" replace /> : <LoginForm onLogin={handleLogin} />} />
          <Route path="/news-preferences" element={!isLoggedIn ? <Navigate to="/login" replace /> : <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />} />
          <Route path="/website" element={<Website selectedCategories={selectedCategories} newsData={articles} />} />
          <Route path="/" element={isLoggedIn ? <Navigate to="/news-preferences" replace /> : <Navigate to="/login" replace />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
