import './App.css';
import Chatbot from './components/Chatbot/Chatbot.js';
import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';  // Importing the NewsPreferencesForm component
import { mockData } from './mockData';  // Import mock data
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import RegistrationForm from './components/RegistrationForm.js';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';


const App = () => {
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isChatbot, setIsChatbot] = useState(false);
  const [hasSelectedPreferences, setHasSelectedPreferences] = useState(false);  // New state for tracking preference selection
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [articles, setArticles] = useState([]);
  const [filteredCategory, setFilteredCategory] = useState(null);
  const [isRegistered, setIsRegistered] = useState(false);


  const fetchArticles = () => {
    axios.get('http://127.0.0.1:5000/api/data',{
      headers: {
        'Content-Type': 'application/json'
      }})
      .then(response => {
        setArticles(response.data);
        console.log(response);
      })
      .catch(error => {
        console.error('There was an error fetching the data:', error);
      });
  };
  useEffect(() => {
    fetchArticles();
  }, []); // Empty array means this useEffect runs once when component mounts



  // Function to fetch articles based on selected categories
  const fetchArticlesByCategory = (categories) => {
    // Logic to filter articles based on selected categories
    // You might need to adjust the API endpoint or parameters based on your backend implementation
    const query = categories.map(category => `category=${category}`).join('&');
    axios.get(`http://127.0.0.1:5000/api/articles?${query}`, {
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      setArticles(response.data);
      console.log(response);
    })
    .catch(error => {
      console.error('There was an error fetching the articles:', error);
    });
  };

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    // Assume if username is "chatbot" then show chatbot, else show website
    setIsChatbot(username.toLowerCase() === 'chatbot');
  };

  const handleSubmit = () => {
    onPreferencesSubmit(selectedCategories);
    // Assuming you pass a function to fetch articles based on selected categories
    fetchArticles(selectedCategories);
};

const handlePreferencesSubmit = (selectedCategories) => {
  setSelectedCategories(selectedCategories);
  fetchArticlesByCategory(selectedCategories); // Fetch articles based on selected categories
};
  // useEffect(() => {
  //   // Fetch the message from the Flask server
  //   axios.get('http://127.0.0.1:5000/:5000/api/hello')
  //   .then(response => {
  //       console.log(response.data);
  //   })
  //   .catch(error => {
  //       console.error('There was an error fetching the data:', error);
  //   })
  // }
  // );
  useEffect(() => {
  const checkUserStatus = () => {
    // Example check (you will need to implement the actual logic)
    const userLoggedIn = localStorage.getItem('userLoggedIn');
    const userPreferences = localStorage.getItem('userPreferences');
    if (userLoggedIn) {
      setIsLoggedIn(true);
      if (userPreferences) {
        setSelectedCategories(JSON.parse(userPreferences));
      }
    }
  };

  checkUserStatus();
  fetchArticles();
  }, []); // Empty array means this useEffect runs once when component mounts

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          {message}
        </header>
        <div className="format">
          <Routes>
          <Route path="/register" element={<RegistrationForm />} />            
          <Route path="/login" element={isLoggedIn ? <Navigate to="/news-preferences" replace /> : <LoginForm onLogin={handleLogin} />} />
          <Route path="/news-preferences" element={!isLoggedIn ? <Navigate to="/login" replace /> : <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />} />
            {/* Define other routes here */}
            {/* Navigate from root to either login or news-preferences based on isLoggedIn */}

          <Route path="/" element={isLoggedIn ? <Navigate to="/news-preferences" replace /> : <Navigate to="/login" replace />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};


export default App;
