import React, { useState } from 'react';
import '../styles/Website.css';
import NewsButtons from './NewsButtons.js';

const Website = ({ selectedtags, articles }) => {
    const [selectedArticleId, setSelectedArticleId] = useState([]);
    console.log(articles)
    const handleArticleClick = (id) => {
        setSelectedArticleId(prevId => prevId === id ? null : id);
    };


    return (
        <div className="website-container">
            <NewsButtons selectedtags={selectedtags} />
                <ul className="news-list">
                        { articles.map((article, index) => {
                        // Destructure the article information based on your data structure
                        const [id, title, link, summary, tag, imageUrl] = article;

                        // Then use these constants to build your list items
                        // Remember to use 'key' prop when mapping to ensure React can track each element uniquely
                        return (
                        <li key={id || index} className={`news-article ${selectedArticleId === article.id ? 'open' : ''}`} onClick={() => handleArticleClick(article.id)}>
                        <div className="header">
                        <h2>{title}</h2>
                        </div>
                        {selectedArticleId === article.id && (
                            <>
                                <div className="image-container">
                                    {/* Conditional rendering if image exists */}
                                    {imageUrl && <img src={imageUrl} alt={title} />}
                                </div>
                                <p>{summary}</p>
                            </>
                        )}
                    </li>
                );
            })}
          </ul>
        </div>
    );
};

export default Website;
