import React, { useState } from 'react';
import '../components/styles/NewsPreferences.css';  // Importing a stylesheet

const NewsPreferences = ({ onPreferencesSubmit }) => {
    const [selectedCategories, setSelectedCategories] = useState([]);

    const handleCategoryToggle = (category) => {
        setSelectedCategories(prevState => {
            return prevState.includes(category) ?
                prevState.filter(cat => cat !== category) :
                [...prevState, category];
        });
    };

    const handleSubmit = () => {
        onPreferencesSubmit(selectedCategories);
    };

    return (
        <div className="preferences-form">
            <h2>Selecteer uw favoriete nieuwscategorieÃ«n</h2>
            <div className="categories">
                {['Politiek', 'Sport', 'Technologie', 'Amusement'].map(category => (
                    <button
                        key={category}
                        className={`category-button ${selectedCategories.includes(category) ? 'selected' : ''}`}
                        onClick={() => handleCategoryToggle(category)}
                    >
                        {category}
                    </button>
                ))}
            </div>
            <button className="submit-button" onClick={handleSubmit}>Submit</button>
        </div>
    );
};

export default NewsPreferences;
