import React from 'react';
import '../styles/Website.css';  // Importing a stylesheet
import NewsButtons from './NewsButtons.js';  // Importing the NewsButtons component

const Website =({ selectedCategories, newsData }) => {

    return (
        <div className="website-container">
            <NewsButtons selectedCategories={selectedCategories}/>
            <ul className="news-list">
                {newsData.map(article => (
                    <div key={article.id} className="news-article">
                        <h2>{article.title}</h2>
                        <p>{article.content}</p>
                    </div>
                ))}
            </ul>
        </div>
    );
};

export default Website;
