import React, { useState } from 'react';
import '../styles/Website.css';
import NewsButtons from './NewsButtons.js';

const Website = ({ selectedCategories, newsData }) => {
    const [filteredCategory, setFilteredCategory] = useState(null);

    const filterArticlesByCategory = (category) => {
        setFilteredCategory(category);
    };

    return (
        <div className="website-container">
            <NewsButtons selectedCategories={selectedCategories} onCategoryClick={filterArticlesByCategory} />
            <ul className="news-list">
                {newsData.filter(article => filteredCategory ? article.category === filteredCategory : true).map(article => (
                    <div key={article.id} className="news-article">
                        <h2>{article.title}</h2>
                        <p>{article.content}</p>
                    </div>
                ))}
            </ul>
        </div>
    );
};

export default Website;
