import './App.css';
import Chatbot from './components/Chatbot/Chatbot.js';
import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';  // Importing the NewsPreferencesForm component

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  const [message, setMessage] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isChatbot, setIsChatbot] = useState(false);
  const [hasSelectedPreferences, setHasSelectedPreferences] = useState(false);  // New state for tracking preference selection
  const [selectedCategories, setSelectedCategories] = useState([]);  // New state for selected categories

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    // Assume if username is "chatbot" then show chatbot, else show website
    setIsChatbot(username.toLowerCase() === 'chatbot');
  };
  const handlePreferencesSubmit = (selectedCategories) => {
    console.log('Selected Categories:', selectedCategories);
    setHasSelectedPreferences(true);
};
  useEffect(() => {
    // Fetch the message from the Flask server
    axios.get('/api/hello')
      .then(response => {
        setMessage(response.data.message);
      });
  }, []);

  return (
    <div class="App">
      <header class="App-header">
        {message}
      </header>
      <div class = "format">
      {!isLoggedIn ? (
        <LoginForm onLogin={handleLogin} />
      ) : !hasSelectedPreferences ? (
          <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />
      ) : isChatbot ? (
        <Chatbot selectedCategories={selectedCategories}/>
      ) : (
        <Website  />
      )}
      </div>
    </div>
  )
};

export default App;
