import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useParams } from 'react-router-dom';
// import LoginForm from './components/Login.js';
import Website from './components/Website/Website.js';
import NewsPreferences from './components/NewsPreferences';
import RegistrationForm from './components/RegistrationForm.js';
import UserProfile from './components/Profile.js';
import userIcon from './components/images/user.svg';
import Chatbot from './components/Chatbot/Chatbot.js';

import './App.css';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [selectedtags, setSelectedtags] = useState([]);
  const [articles, setArticles] = useState([]);
 // State variables for query parameters
 const [queryParams, setQueryParams] = useState({});



 useEffect(() => {
  const searchParams = new URLSearchParams(window.location.search);
  const params = {
    id: searchParams.get('id'),
    age: searchParams.get('age'),
    location: searchParams.get('location'),
    gender: searchParams.get('gender'),
    education: searchParams.get('education'),
    condition: searchParams.get('condition'),
  };
  setQueryParams(params);

  handleLogin(params);
}, []);

const handleLogin = (params) => {
  const allParamsPresent = Object.values(params).every(param => param !== null);

  if (allParamsPresent) {
    setIsLoggedIn(true);
  } else {
    console.error('Missing required URL parameters.');
  }
};

const handlePreferencesSubmit = (tags) => {
    setSelectedtags(tags);
    axios.post('http://127.0.0.1:5001/api/recommend', { tags: tags })
      .then(response => {
        // Handle the response containing the recommendations
        setArticles(response.data.similar_articles);
      })
      .catch(error => {
        console.error('Error fetching recommendations:', error);
      });
  };

  // Redirect based on condition parameter
  const renderRedirect = () => {
    if (queryParams.condition === 'condition1') {
      return <Chatbot />;
    } else if (queryParams.condition === 'condition2') {
      return <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />;
    } else {
      // Assuming handlePreferencesSubmit is defined in your component
      return <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />;
    }
  };

  return (
    <Router>
      <div className="App">
        {isLoggedIn && (
          <header className='header'>
            <nav className='nav'>
              <ul>
                <li className='home'><Link to="/">Home</Link></li>
                {queryParams.condition !== 'condition1' && <li className='website'><Link to="/website">Website</Link></li>}
                {queryParams.condition !== 'condition2' && <li className='chatbot'><Link to="/chatbot">Chatbot</Link></li>}
                <li className='profile'><Link to="/profile"><img src={userIcon} alt="User Icon" /></Link></li>
              </ul>
            </nav>
          </header>
        )}
        <Routes>
          <Route path="/register" element={<RegistrationForm />} />
          <Route path="/news-preferences" element={<NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />} />
          {/* <Route path="/website" element={<Website selectedtags={selectedtags} articles={articles} />} /> */}
          <Route path="/chatbot" element={<Chatbot selectedtags={selectedtags} articles={articles} />} />
          <Route path="/" element={isLoggedIn ? (queryParams.condition === 'condition1' ? <Chatbot /> : queryParams.condition === 'condition2' ? <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} /> : <NewsPreferences onPreferencesSubmit={handlePreferencesSubmit} />) : <Navigate to="/login" replace />} />
          <Route path="/website/:id/:age/:location/:gender/:education/:condition" element={<Website />} />
          <Route path="/profile" element={<UserProfile />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
