import React, { useState } from 'react';
import '../styles/Website.css';
import NewsButtons from './NewsButtons.js';

const Website = ({ selectedCategories, newsData, fetchArticles }) => {
    const [selectedArticleId, setSelectedArticleId] = useState(null);

    const handleArticleClick = (id) => {
        setSelectedArticleId(prevId => prevId === id ? null : id);
    };

    // Assuming each article in newsData has a 'category' field to filter against
    // and that 'selectedCategories' is an array of selected category strings.
    const filteredNewsData = selectedCategories.length > 0
        ? newsData.filter(article => selectedCategories.includes(article.category))
        : newsData;

    return (
        <div className="website-container">
            <NewsButtons selectedCategories={selectedCategories} onCategoryClick={fetchArticles}/>
            <ul className="news-list">
                {filteredNewsData.map(article => (
                    <li key={article.id} className={`news-article ${selectedArticleId === article.id ? 'open' : ''}`} onClick={() => handleArticleClick(article.id)}>
                        <div className="header">
                            <h2>{article.title}</h2>
                        </div>
                        {selectedArticleId === article.id && (
                            <>
                                <div className="image-container">
                                    {/* Conditional rendering if image exists */}
                                    {article.image && <img src={article.image} alt={article.title} />}
                                </div>
                                <p>{article.content}</p>
                            </>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Website;
