import React, { useState } from 'react';
import { useLocation, Navigate } from 'react-router-dom';
import '../styles/Website.css';
import NewsButtons from './NewsButtons.js';

const Website = ({  articles }) => {
    const [selectedArticleId, setSelectedArticleId] = useState(null);
    const [filteredCategory, setFilteredCategory] = useState(null);
    const location = useLocation(); // Use useLocation hook to access location

    const handleArticleClick = (id) => {
        setSelectedArticleId(prevId => prevId === id ? null : id);
    };
    const filterArticlesByCategory = (category) => {
        setFilteredCategory(category);
    };
    const selectedCategories = location.state.selectedCategories; // Get the selectedCategories prop from location

    // Filter articles based on selected categories
    const filteredArticles = filteredCategory
    ? articles.filter((article) => article.category === filteredCategory)
    : articles;
    const splitIntoSentences = (text) => {
        return text.split('.');
      };

    return (
        <div className="website-container">
            <NewsButtons
                    selectedCategories={selectedCategories}
                    onCategoryClick={filterArticlesByCategory}
                />            
                <ul className="news-list">
                {articles.map((article, index) => {
                    // Assuming articles is an array of arrays, with structure based on your previous data
                    const id = index; // Using index as a fallback ID
                    const title = article[6]; // Assuming the first element is the title
                    const content = article[2]; // Assuming the second element is the content
                    const image = article[4]; // Assuming the fourth element is the image URL
                    // Split content into paragraphs based on double line breaks
                    const paragraphs = splitIntoSentences(content);
                    // Get the first paragraph
                    const firstParagraph = paragraphs[0];
                    return (
                        <li key={id} className={`news-article ${selectedArticleId === id ? 'open' : ''}`} onClick={() => handleArticleClick(id)}>
                            <div className="header">
                                <h2>{title}</h2>
                            </div>
                            {selectedArticleId === id && (
                                <>
                                    <div className="image-container">
                                        {/* Conditional rendering if image exists */}
                                        {image && <img src={image} alt={title} />}
                                    </div>
                                    <p>{firstParagraph}</p>
                                </>
                            )}
                        </li>
                    );
                })}
            </ul>
        </div>
    );
};

export default Website;
